mapper.update:
update t_participant
set join_ok='참가수락'
where p_seq=#{p_seq} and prj_seq=#{prj_seq} and t_id=#{t_id}

update t_participant
set join_ok='참가거절'
where p_seq=#{p_seq} and prj_seq=#{prj_seq} and t_id=#{t_id}

update t_participant
set prj_rating =#{prj_rating}
where p_seq=#{p_seq} and prj_seq=#{prj_seq} and t_id=#{t_id}
