mapper.update:
update t_participant
set join_ok='참가수락'
where p_seq=#{p_seq} and prj_seq=#{prj_seq} and t_id=#{t_id}

update t_participant
set join_ok='참가거절'
where p_seq=#{p_seq} and prj_seq=#{prj_seq} and t_id=#{t_id}

update t_project
set join_count = join_count + 1
where prj_seq = #{prj_seq}
and exists (
select 1
from t_participant
where prj_seq = #{prj_seq}
and t_id = #{t_id}
and join_ok = '참가신청'
